                                         // if(sum.round(3) == data_sheet.getCell(5,i).getContents().toFloat().round(3))
                                            // {
                                                   //log.info "2"
                                                    def credit_limit = data_sheet.getCell(7,i).getContents().toFloat().round(3)
                                                    if(sum.round(3) > credit_limit)
                                                       {
                                                          //log.info "FALSE"
