   }

if(log_act_notify=="failed"){
 def Expected="Expected_ACT_Notification >>>" +expectedActNotification+"\r\n"
 def actual= "Found_ACT_Notification >>>" + actualActNotification+"\r\n"
log.error Expected
logFile.append  Expected
