import groovy.json.JsonSlurper
import static com.jayway.jsonpath.JsonPath.parse
import jxl.*

def getAzureResponse = testRunner.testCase.getTestStepByName("REST Request_Azure URL").getPropertyValue("Response")
//log.info getResponce

// Fetch By ID
def CusId = parse(getAzureResponse).read('$..ServiceOrdNo')

def getDenodoResponse = testRunner.testCase.getTestStepByName("REST Request_Denodo URL").getPropertyValue("Response")
def DenodoRes = parse(getDenodoResponse).read('$..sm01001')

for(def i = 0; i <= CusId.size() - 1; i++ )
if( CusId[i] == DenodoRes[i] )
{
	log.info "TS PASS"
	log.info CusId[i]
	log.info DenodoRes[i]

}
else
{
	log.error "TS FAIL"
}

