con:config.script:
import groovy.json.JsonSlurper

Date date=new Date()
def title=("\r\n"+date+"Executing>>>>>>>>>>>>>>>>>>>[OOVS][MO][ACT]"+context.getCurrentStep().getLabel()+"\r\n")
log.info title


def logFile=context.resultfile

// get a reference to the common util  TestSuite
def logapi = context.logAPI
//Dataset class object
def datasetobject = context.dataSet

int tc=0
int pass=0
int fail=0

Map dataset  = new HashMap();
Map usersubmap  = new HashMap();

for (int i=1;i<= context.rowCount;i++)
{
	dataset=datasetobject.getRowDataMap(i)	
  	if(dataset.get("isexecutable")) 
	{    tc++
		msisdn= dataset.get("userid")

         String telnumber="tel:"+ msisdn.toString()
          //reset user 		
		datasetobject.resetUser(msisdn)

		if( !dataset.get("isfreetrial"))
		{
		//make user direct paid eligible
          datasetobject.createDirectPaidUser(msisdn,"1202000024")
		}

	    def 	propertyMO= testRunner.testCase.getTestStepByName("Properties")
	     //To set Parameter Values  of Activation API 	
      	propertyMO.setPropertyValue("action",dataset.get("keyword"))	 	
		propertyMO.setPropertyValue("telnumber",telnumber)

		def 	stepMORx= testRunner.testCase.getTestStepByName("OOVS_MO_Rx")	 
		stepMORx.getHttpRequest().setEndpoint(context.expand('${#Global#oovs_soap_endpoint}'))    	 
	   	stepMORx.run(testRunner,testRunner.getRunContext())	

		//validate DB values       	
 usersubmap= datasetobject.getUserData(msisdn,"1")

//assert the response & db values
if(
usersubmap.get("subscription_status_id").toString() == dataset.get("expid")
&& usersubmap.get("validity").toString() == dataset.get("expval")
&& usersubmap.get("current_billing_code").toString() == dataset.get("expbillcode")
&& usersubmap.get("user_source").toString() == "SMS"
//&&  usersubmap.get("next_billing_validity").toString()==dataset.get("nbd") 
&&  usersubmap.get("credits").toString()==dataset.get("expcredits")
) 
{   
//calling log api test method	

def log_act_notify= logapi.assertBaaSLogAPI(msisdn,"ACTIVATION_CHARGING_NOTIFICATION","3","3",dataset.get("activityres").toString(),dataset.get("expid"),
dataset.get("expbillcode"),"1",dataset.get("prebillcode"),dataset.get("expaccmode"),dataset.get("expchargemode"),dataset.get("isfreetrial").toString()
,dataset.get("integrationtype").toString(),dataset.get("customerid").toString(),dataset.get("providerid").toString(),"40") 

//print result
log.info ("\r\n"+"[OOVS][MO][ACT]"+dataset.get("tcname") +" bass test: passed  >>>>>>>>>> act_charging_notification_log: "+log_act_notify+"\r\n")

if (log_act_notify=="passed") {pass++} 
else if(log_act_notify=="failed") {
	fail++
	log.error "Expected>>>>>>>>>>" +context.expected
   log.error "Found>>>>>>>>>>" + context.actual
   }
   else{fail++}
}

else{ 	
    fail++
def failresult=  ("\r\n"+"***********************************[OOVS][MO][ACT]"+dataset.get("tcname") +"  is Failed" +"******************************************"+"\r\n")
def expected=  ( "\r\n"+"Expected Values:"+"  sub_status_id="+dataset.get("expid")+",Validity="+dataset.get("expval")+",curr_billing_code="+dataset.get("expbillcode")+",user_source=SMS"+" credits="+dataset.get("expcredits")+"\r\n")
def actual=  ("\r\n"+"But Found:"+" sub_status_id="+usersubmap.get("subscription_status_id")+",Validity="+usersubmap.get("validity")+ ",curr_billing_code="+usersubmap.get("current_billing_code")+",user_source=" +usersubmap.get("user_source")+"credits="+usersubmap.get("credits")+"\r\n")
log.error failresult
log.error expected
log.error actual
logFile.append failresult+"\r\n"+expected +"\r\n" + actual
	} }} 

def summary=  ("\r\n"+date+" Execution Summary of [OOVS][MO][ACT] "+context.getCurrentStep().getLabel()+"\r\n" + "Total Executed="+tc +"\r\n"+"Total Pass="+pass +"\r\n"+"Total Fail="+fail+"\r\n")
logFile.append summary
log.info summary
